import React, { Component } from 'react';
import './App.css';
import Navigation from './components/Navigation';
import Particles from "react-particles-js";
import Logo from './components/Logo';
import FaceRecognition from './components/FaceRecognition';
import ImgLinkForm from './components/ImgLinkForm';
import Rank from './components/Rank';
//const Clarifai = require('clarifai');
import Clarifai from 'clarifai';

const app = new Clarifai.App({
  apiKey: '09f7fc57537a44df828b43038f1c4e57',
});

const param = {
  number: {
    value: 10,
    density: {
      enable: true,
      value_area: 1000
    }
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: '',
      imgURL : '',
      box: {} 
    };
  }

  calFaceBox = (data) => {
    const cal = data[0].region_info.bounding_box
    const image = document.getElementById('image');
    const width = Number(image.width);
    const height = Number (image.height);
    return {
      leftCol : cal.left_col * width,
      topRow : cal.top_row * height,
      rightCol : width - (cal.right_col * width),
      bottomROw : height - (cal.bottom_row * height)
    }
  }

  onChangeURL = (event) => {
    this.setState({ input: event.target.value });
  }

  onSubmit = () => {
    this.setState((state) => ({
      imgURL: state.input
    }));
    app.models.predict("a403429f2ddf4b49b307e318f00e528b",this.state.input).then(
      function (response) {
        let res = response.outputs[0].data.regions
        //console.log(res);
        this.calFaceBox(res);
      },
      function (err) {
      }
    );
  }

  render() {
    let imgURL = this.state.imgURL;
    return (
      <div className="App">
        <Particles className='particles' params={param}/>
        <Navigation />
        <Logo/>
        <Rank/>
        <ImgLinkForm onChangeURL={this.onChangeURL} onSubmit={this.onSubmit}/>
        <FaceRecognition url= {imgURL}/>
      </div>
    );
  }
}

export default App;

